let imgbox =document.querySelector(".imgbox");
let imgmain =document.querySelector(".imgmain");
let imgitems =document.querySelectorAll(".imgmain > img");
let last =document.querySelector(".last");
let next =document.querySelector(".next");

let curr =9;

next.onclik=function (){
	curr++;
	imgmain.style.left= - curr * 1100+"px";
	if(curr>=imgitems.length){
		curr=0;
		imgmain.style.left="0px";
	}
}
last.onclik=function(){
	curr--;
	imgmain.style.left=-curr*1100+"px";
	if(curr<0){
		curr=imgitems.length-1;
		imgmain.style.left="-3300px";
	}
}

let time=setInterval(()=>{
	next.onclick ();
},2000);

box.onmouseenter=function(){
	clearInterval(time);
}
box.onmouseleft=function(){
	time=setInterval(()=>{
		next.onclick();
	},2000);
}